import React,{useState,useEffect} from "react";
import {Redirect} from "react-router-dom";
import { cartEmpty } from "./helper/carthelper";
import {getmeToken ,processPayment} from "./helper/paymenthelper"
import {createOrder} from "./helper/orderhelper"
import { isAuthenticated ,signout} from "../auth/helper";

import DropIn from "braintree-web-drop-in-react"

const PaymentB=({
products,
reload=undefined,
setReload=(f)=>f,

})=>{

    const [info,setInfo]=useState({
        loading:false,
        success:false,
        clientToken:null,
        error:"",
        instance:{},
    });

const userId=isAuthenticated && isAuthenticated().user.id;
const token= isAuthenticated && isAuthenticated().token;

const getToken=(userId,token)=>{
    getmeToken(userId,token)
    .then((info)=>{
        console.log(info)
        
        if(info.error){
            setInfo({
                ...info,
                error:info.error
            })
            signout(()=>{
                return <Redirect to="/" />
            })
        }
        else{
            const clientToken=info.clientToken;
            setInfo({clientToken});
        }
    })
};

useEffect(()=>{
    getToken(userId,token);
},[]);

const getAmount=()=>{
    let amount=0;
    products.map((p)=>{
        amount=amount+parseInt(p.price);
    });
    return amount;
};

const onPurchase=()=>{
    setInfo({loading:true})
    let nonce;
    let getNonce=info.instance.requestPaymentMethod()
    .then(data=>{
        nonce=data.nonce;
        const paymentData={
            paymentMethodNonce:nonce,
            amount:getAmount()
        };
        processPayment(userId,token,paymentData)
        .then(response =>{
            if(response.error){
                if(response.code=="1"){
                    console.log("payment failed")
                    signout(()=>{
                        return <Redirect to="/"/>
                    })
                }
            }
            else{
                setInfo({...info,
                success:response.success,loading:false
                })
                console.log("payment success")
                let product_names=""
                products.forEach(function(item){
                    product_names+=item.name+", "
                });
                const orderData={
                    products:product_names,
                    transaction_id:response.transaction.id,
                    amount: response.transaction.amount
                }
                createOrder(userId,token,orderData)
                .then(response=>{
                    if(response.error){
                        if(response.code=="1"){
                            console.log("order failed")
                        }
                      signout(()=>{
                          return <Redirect to="/"/>
                      })
                                        }

                       else{
                           if(response.success==true){
                               console.log("order placed")
                           }
                       }                 
                })
                .catch(error =>{
                    setInfo({loading:false,success:false})
                    console.log("order failed",error)
                })
                cartEmpty(()=>{
                    console.log("cart is emptyed out")
                })
                setReload(!reload)
            }
        })
        .catch(e =>console.log(e))
    })
    .catch(e=>console.log("Nonce",e))
}








const showbtnDropIn=()=>{
    return(
        <div>
            {
                info.clientToken !==null&& products.length >0?
                (
                    <div>
                        <DropIn
                        options={{authorization:info.clientToken}}
                        onInstance={instance =>(info.instance=instance)}
                        >
                             </DropIn>
                    <button onClick={onPurchase} className="btn btn-block btn-success">Buy now</button>
                       
</div>
                ):
                (
                    <h3>Please login fisrt or add something in cart</h3>
                )
            }
        </div>
    )
}


return(
    <div>
        <h3>your bill is {getAmount()} 
        </h3>
        {showbtnDropIn()}
    </div>

)
  


};
export default PaymentB;
